<?phpclass objtProducto {    public $idProduct;    public $nombre;    public $precio;    public $tipo;    public $img;    public $cantidad;    public function __construct($idProduct, $nombre, $precio, $tipo, $img, $cantidad = 1) {        $this->idProduct = $idProduct;        $this->nombre = $nombre;        $this->precio = $precio;        $this->tipo = $tipo;        $this->img = $img;        $this->cantidad = $cantidad;    }    function getIdProduct() {        return $this->idProduct;    }    function getNombre() {        return $this->nombre;    }    function getPrecio() {        return $this->precio;    }    function getTipo() {        return $this->tipo;    }    function getImg() {        return $this->img;    }    function getCantidad() {        return $this->cantidad;    }    function setCantidad($cantidad) {        $this->cantidad = $cantidad;    }}